import {makeStyles} from '@material-ui/core/styles'

export default makeStyles({
    container:{
        padding: '3% 5%',
        width: '100%',
        margin: '0'
    },
    card: {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        alignItems:'flexstart',
        borderBottom: '10px solid #04071A',       
        width:'100%',
        height: '45vh', 
        padding:'10%',
        borderRadius: 10,
        color:'white'
    },
    infoCard:{
        display: 'flex',
        flexDirection: 'column',
        // textAlign: 'center',  
    }
})
